package shop;

public class Seller {

	private String id;
	private String name;
	private String email;
	
	public Seller(String id, String name, String email) {
		this.id = id;
		this.name = name;
		this.email = email;
	}

	public String getId() {
		// TODO Auto-generated method stub
		return this.id;
	}

	public String getName() {
		// TODO Auto-generated method stub
		return this.name;
	}

	public String getEmail() {
		// TODO Auto-generated method stub
		return this.email;
	}

}
