package unitils;

public class Book {

	private String name;
	private String author;
	private int price;
	
	public Book(String name, String author, int price) {
		this.name = name;
		this.author = author;
		this.price = price;
	}

	public String getName() {
		// TODO Auto-generated method stub
		return this.name;
	}

	public String getAuthor() {
		// TODO Auto-generated method stub
		return this.author;
	}

	public int getPrice() {
		// TODO Auto-generated method stub
		return this.price;
	}

}
